import { Component, Renderer2 } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-services-product',
  templateUrl: './services-product.component.html',
  styleUrls: ['./services-product.component.css']
})
export class ServicesProductComponent {

  private scrollAnimationElement: HTMLElement;

  constructor(private route: ActivatedRoute, private renderer: Renderer2, private el: ElementRef) {}

  ngOnInit() {
    this.route.fragment.subscribe(fragment => {
      if (fragment) {
        const element = document.querySelector('#' + fragment);
        if (element) {
          element.scrollIntoView();
        }
      }
    });

    window.addEventListener('scroll', () => {
      const scrollAnimation = document.querySelector('.rotate') as HTMLElement | null;

      if (scrollAnimation) {
        const scrollPos = window.innerHeight - scrollAnimation.getBoundingClientRect().top;

        if (scrollPos > 0) {
          scrollAnimation.classList.add('animate');
        }
      }
    });
  }
}
