import { Component, OnInit, Input } from '@angular/core';

export interface IAlert {
    id: number;
    type: string;
    strong?: string;
    message: string;
    icon?: string;
}

@Component({
  selector: 'app-alerts-section',
  templateUrl: './alerts-section.component.html',
  styleUrls: ['./alerts-section.component.css']
})
export class AlertsSectionComponent {
  @Input()
  public alerts: Array<IAlert> = [];
  private backup: Array<IAlert>;
  constructor() {
      this.alerts.push({
          id: 1,
          type: 'success',
          strong: 'Success!',
          message: 'This is a success alert—check it out!',
          icon: 'ni ni-like-2'
      }, {
          id: 2,
          strong: 'Info!',
          type: 'info',
          message: 'This is an info alert—check it out!',
          icon: 'ni ni-bell-55'
      }, {
          id: 3,
          type: 'warning',
          strong: 'Warning!',
          message: 'This is a warning alert—check it out!',
          icon: 'ni ni-bell-55'
      }, {
          id: 4,
          type: 'danger',
          strong: 'Danger!',
          message: 'This is a danger alert—check it out!',
          icon: 'ni ni-support-16'
      });
      this.backup = this.alerts.map((alert: IAlert) => Object.assign({}, alert));
  }

  close(alert: IAlert) {
    this.alerts.splice(this.alerts.indexOf(alert), 1);
  }

}

<div class="container">
  <h3 class="mt-lg mb-4">
    <span>Alerts</span>
  </h3>
  <div *ngFor="let alert of alerts">
    <ngb-alert [type]="alert.type" [dismissible]="true"  (close)="close(alert)">
      <ng-container *ngIf="alert.icon">
        <div class="alert-inner--icon">
            <i class="{{alert.icon}}"></i>
        </div>
      </ng-container>
      <span class="alert-inner--text">  <strong>{{alert.strong}} </strong>{{ alert.message }}</span>
    </ngb-alert>
  </div>
</div>
