import { Component } from '@angular/core';

@Component({
  selector: 'app-buy',
  templateUrl: './buy.component.html',
  styleUrls: ['./buy.component.css']
})
export class BuyComponent {

ngOnInit() {
  const button = document.getElementById('myButton') as HTMLButtonElement;
  const container = document.getElementById('container') as HTMLElement;
  const oldInfo = document.querySelector('.old-info') as HTMLElement;
  const newInfo = document.querySelector('.new-info') as HTMLElement;

  button.addEventListener('click', () => {
    container.classList.add('transitioning');

    setTimeout(() => {
      oldInfo.style.transform = 'translateY(-100%)';
      oldInfo.style.opacity = '0';

      setTimeout(() => {
        oldInfo.style.display = 'none';

        newInfo.style.display = 'block';
        newInfo.style.transform = 'translateY(100%)';
        newInfo.style.opacity = '0';

        setTimeout(() => {
          newInfo.style.transform = 'translateY(0)';
          newInfo.style.opacity = '1';

          setTimeout(() => {
            container.classList.remove('transitioning');
          }, 100);
        }, 10);
      }, 500); // Ajusta el tiempo según la duración de la animación CSS (0.5s en este caso)
    }, 10);
  });
}

}
