import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-nucleo-section',
  templateUrl: './nucleo-section.component.html',
  styleUrls: ['./nucleo-section.component.css']
})
export class NucleoSectionComponent implements OnInit {

  constructor() { }

  ngOnInit() {
    var nucleoView = document.getElementsByClassName('icons-container')[0];
    window.addEventListener('scroll', function (event) {
    	if (this.isInViewport(nucleoView)) {
    		nucleoView.classList.add('on-screen');
    	}
      else{
        nucleoView.classList.remove('on-screen');
      }
    }.bind(this), false);
  }
  isInViewport(elem) {
      var bounding = elem.getBoundingClientRect();
      return (
          bounding.top >= 0 &&
          bounding.left >= 0 &&
          bounding.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
          bounding.right <= (window.innerWidth || document.documentElement.clientWidth)
      );
  };

}
<section class="section section-lg section-nucleo-icons pb-250">
  <div class="container">
    <div class="row justify-content-center">
      <div class="col-lg-8 text-center">
        <h2 class="display-3">Nucleo Icons</h2>
        <p class="lead">
          The official package contains over 21.000 icons which are looking great in combination with Argon Design System. Make sure you check all of them and use those that you like the most.
        </p>
        <div class="btn-wrapper">
          <a href="https://demos.creative-tim.com/argon-design-system-angular/documentation/icons?ref=adsa-nucleo-section" class="btn btn-primary">View demo icons</a>
          <a href="https://nucleoapp.com/?ref=1712" target="_blank" class="btn btn-default mt-3 mt-md-0">View all icons</a>
        </div>
      </div>
    </div>
    <div class="blur--hover">
      <a href="https://demos.creative-tim.com/argon-design-system-angular/documentation/icons?ref=adsa-nucleo-section">
        <div class="icons-container blur-item mt-5" data-toggle="on-screen">
          <!-- Center -->
          <i class="icon ni ni-diamond"></i>
          <!-- Right 1 -->
          <i class="icon icon-sm ni ni-album-2"></i>
          <i class="icon icon-sm ni ni-app"></i>
          <i class="icon icon-sm ni ni-atom"></i>
          <!-- Right 2 -->
          <i class="icon ni ni-bag-17"></i>
          <i class="icon ni ni-bell-55"></i>
          <i class="icon ni ni-credit-card"></i>
          <!-- Left 1 -->
          <i class="icon icon-sm ni ni-briefcase-24"></i>
          <i class="icon icon-sm ni ni-building"></i>
          <i class="icon icon-sm ni ni-button-play"></i>
          <!-- Left 2 -->
          <i class="icon ni ni-calendar-grid-58"></i>
          <i class="icon ni ni-camera-compact"></i>
          <i class="icon ni ni-chart-bar-32"></i>
        </div>
        <span class="blur-hidden h5 text-success">Eplore all the 21.000+ Nucleo Icons</span>
      </a>
    </div>
  </div>
</section>
