import { Component } from '@angular/core';

@Component({
  selector: 'app-buy',
  templateUrl: './buy.component.html',
  styleUrls: ['./buy.component.css']
})
export class BuyComponent {

  ngOnInit() {
    const messageLink = document.querySelector('.message') as HTMLElement;
    const form = document.querySelector('form') as HTMLElement;

messageLink.addEventListener('click', () => {
  const formStyles = getComputedStyle(form);
  const formHeight = parseInt(formStyles.height);
  const formOpacity = parseInt(formStyles.opacity);

  const toggleHeight = formHeight === 0 ? 'toggle' : 0;
  const toggleOpacity = formOpacity === 0 ? 'toggle' : 0;

  form.animate({ height: toggleHeight, opacity: toggleOpacity }, 'slow' as KeyframeAnimationOptions);
});
  }
}

<body style="display:flex; align-items:center; justify-content:center;">
<div class="login-page">
  <div class="form">
    <form class="register-form" method="POST">
      <h2>Register</h2>
      <input type="text" placeholder="Full Name *" required/>
      <input type="text" placeholder="Username *" required/>
      <input type="email" placeholder="Email *" required/>
      <input type="password" placeholder="Password *" required/>
      <a class="btn" href="#">
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        Create
      </a>
      <p class="message">Already registered? <a href="#">Sign In</a></p>
    </form>
    <form class="login-form" method="post">
      <h2> Login</h2>
      <input type="text" placeholder="Username" required />
      <input type="password" placeholder="Password" required/>
      <a class="btn" href="#">
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        Sign in
      </a>
      <p class="message">Not registered? <a href="">Create an account</a></p>
    </form>
  </div>
</div>
<script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
</body>
